package Ex;

public class 문자열 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s = "i lOVE You";
		System.out.println(s);
		//문자열의 길이
		System.out.println(s.length());
		//대소문자 변환
		System.out.println(s.toLowerCase());
		System.out.println(s.toUpperCase());
		
		//포함관계
		System.out.println(s.contains("Java")); // contains : 포함되면 true 아니면 false"
		System.out.println(s.contains("i lO"));
		System.out.println(s.indexOf("lOVE")); // indexOf : 위치정보
		System.out.println(s.indexOf("c#")); //주어진 값이 포함되지 않는다면 -1 반환
		System.out.println(s.lastIndexOf("You"));
		
		
		System.out.println(s.startsWith("i lOVE"));
		
		 
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
